// Home.jsx
import React from 'react';
import styled from 'styled-components';
import heroBg from '../assets/herobghome.png';
import homeImage1 from '../assets/homeimage1.png';
import { Link } from 'react-router-dom';

const HeroSection = styled.section`
  background: url(${heroBg}) no-repeat center center;
  background-size: cover;
  height: 377px;
  width: 100vw;
  margin-left: calc(-50vw + 50%);
  margin-right: calc(-50vw + 50%);
  position: relative;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  overflow: hidden;
`;

const ContentWrapper = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  gap: 20px;
  max-width: 1552px;
  width: 100%;
  padding: 0 20px;
`;

const WelcomeText = styled.h1`
  width: 100%;
  max-width: 1465px;
  height: 120px;
  font-family: 'Avenir Next', sans-serif;
  font-weight: 700;
  font-size: 80px;
  line-height: 150%;
  letter-spacing: -2.2%;
  color: #FFFFFF;
  text-align: center;
  margin: 0;
`;

const SubTitle = styled.h2`
  width: 100%;
  max-width: 1064px;
  height: 69px;
  font-family: 'Avenir Next', sans-serif;
  font-weight: 700;
  font-style: italic;
  font-size: 46px;
  line-height: 150%;
  letter-spacing: -1.9%;
  color: #FFFFFF;
  text-align: center;
  margin: 0;
`;

const ExploreButton = styled(Link)`
  width: 304px;
  height: 68px;
  background: linear-gradient(90deg, #FF9E42 0%, #FFC287 100%);
  border-radius: 6px;
  display: flex;
  justify-content: center;
  align-items: center;
  text-decoration: none;
  margin-top: 20px;
`;

const ButtonText = styled.span`
  width:290px;
  height: 36px;
  font-family: 'Avenir Next', sans-serif;
  font-weight: 700;
  font-size: 19px;
  line-height: 150%;
  letter-spacing: -1.9%;
  color: #FFFFFF;
  text-align: center;
`;

const AboutContainer = styled.div`
  width: 100%;
  padding: 200px 0;
  display: flex;
  justify-content: center;
`;

const AboutSection = styled.section`
  width: 1258px;
  height: 431px;
  background: #EBF6FF;
  border-radius: 6px;
  position: relative;
  padding: 57px 55px;
  box-sizing: border-box;
  margin-left: -100px;
`;

const AboutHeading = styled.h2`
  width: 825px;
  height: 72px;
  font-family: 'Avenir Next', sans-serif;
  font-weight: 700;
  font-size: 48px;
  line-height: 150%;
  letter-spacing: -2.2%;
  color: #000000;
  margin: 0;
`;

const AboutText = styled.p`
  width: 949px;
  height: 180px;
  font-family: 'Avenir Next', sans-serif;
  font-weight: 400;
  font-size: 24px;
  line-height: 150%;
  letter-spacing: -1.9%;
  color: #000000;
  margin-top: 8px;
`;

const WhyUsButton = styled(Link)`
  width: 168px;
  height: 68px;
  background: linear-gradient(90deg, #FF9E42 0%, #FFC287 100%);
  border-radius: 6px;
  display: flex;
  justify-content: center;
  align-items: center;
  text-decoration: none;
  margin-top: 27px;
`;

const AboutImage = styled.img`
  width: 463px;
  height: 455.25px;
  position: absolute;
  top: -34.25px;
  right: -205px;
  object-fit: cover;
`;

const Home = () => {
  return (
    <>
      <HeroSection>
        <ContentWrapper>
          <WelcomeText>Welcome to Anitha Solutions.</WelcomeText>
          <SubTitle>"An Unique IT Solution"</SubTitle>
          <ExploreButton to="/about">
            <ButtonText>Explore More About Us</ButtonText>
          </ExploreButton>
        </ContentWrapper>
      </HeroSection>

      <AboutContainer>
        <AboutSection>
          <AboutHeading>About Anitha Solutions</AboutHeading>
          <AboutText>
            Anitha Solutions is a technology consulting and support company dedicated to helping 
            businesses meet their challenges. We believe technology solutions should solve 
            problems, not create them. We provide a full range of information technology services. 
            Anitha Solutions has been a leading provider of Software services and solutions dedicated 
            to helping businesses meet their challenges.
          </AboutText>
          <WhyUsButton to="/about">
            <ButtonText>Why Us?</ButtonText>
          </WhyUsButton>
          <AboutImage src={homeImage1} alt="About Anitha Solutions" />
        </AboutSection>
      </AboutContainer>
    </>
  );
};

export default Home;
