// Home.jsx
import React from 'react';
import styled from 'styled-components';
import heroBg from '../assets/herobghome.png';
import { Link } from 'react-router-dom';


const HeroSection = styled.section`
  background: url(${heroBg}) no-repeat center center;
  background-size: cover;
  min-height: 377px;
  width: 1552px;
  display: flex;
  flex-direction: column;
  align-items: center;
  position: relative;
  padding-top: 339px;
`;

const WelcomeText = styled.h1`
  width: 1465px;
  height: 120px;
  font-family: 'Avenir Next', sans-serif;
  font-weight: 700;
  font-size: 80px;
  line-height: 150%;
  letter-spacing: -2.2%;
  color: #FFFFFF;
  text-align: center;
  margin: 0;
`;

const SubTitle = styled.h2`
  width: 1064px;
  height: 69px;
  margin-top: 0;
  font-family: 'Avenir Next', sans-serif;
  font-weight: 700;
  font-style: italic;
  font-size: 46px;
  line-height: 150%;
  letter-spacing: -1.9%;
  color: #FFFFFF;
  text-align: center;
`;

const ExploreButton = styled(Link)`
  width: 304px;
  height: 68px;
  margin-top: 29px;
  background: linear-gradient(90deg, #FF9E42 0%, #FFC287 100%);
  border-radius: 6px;
  display: flex;
  justify-content: center;
  align-items: center;
  text-decoration: none;
  transition: transform 0.2s ease;

  &:hover {
    transform: scale(1.05);
  }
`;

const ButtonText = styled.span`
  width: 262px;
  height: 36px;
  font-family: 'Avenir Next', sans-serif;
  font-weight: 700;
  font-size: 24px;
  line-height: 150%;
  letter-spacing: -1.9%;
  color: #FFFFFF;
  text-align: center;
`;

const Home = () => {
  return (
    <HeroSection>
      <WelcomeText>Welcome to Anitha Solutions.</WelcomeText>
      <SubTitle>"An Unique IT Solution"</SubTitle>
      <ExploreButton to="/about">
        <ButtonText>Explore More About Us</ButtonText>
      </ExploreButton>
    </HeroSection>
  );
};

export default Home;
