// Home.jsx
import React from 'react';
import styled from 'styled-components';
import heroBg from '../assets/herobghome.png';
import { Link } from 'react-router-dom';

const HeroSection = styled.section`
  background: url(${heroBg}) no-repeat center center;
  background-size: cover;
  height: 377px;
  width: 100vw;
  margin-left: calc(-50vw + 50%);
  margin-right: calc(-50vw + 50%);
  position: relative;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  overflow: hidden;
`;

const ContentWrapper = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  gap: 20px;
  max-width: 1552px;
  width: 100%;
  padding: 0 20px;
`;

const WelcomeText = styled.h1`
  width: 100%;
  max-width: 1465px;
  height: 120px;
  font-family: 'Avenir Next', sans-serif;
  font-weight: 700;
  font-size: 80px;
  line-height: 150%;
  letter-spacing: -2.2%;
  color: #FFFFFF;
  text-align: center;
  margin: 0;
`;

const SubTitle = styled.h2`
  width: 100%;
  max-width: 1064px;
  height: 69px;
  font-family: 'Avenir Next', sans-serif;
  font-weight: 700;
  font-style: italic;
  font-size: 46px;
  line-height: 150%;
  letter-spacing: -1.9%;
  color: #FFFFFF;
  text-align: center;
  margin: 0;
`;

const ExploreButton = styled(Link)`
  width: 304px;
  height: 68px;
  background: linear-gradient(90deg, #FF9E42 0%, #FFC287 100%);
  border-radius: 6px;
  display: flex;
  justify-content: center;
  align-items: center;
  text-decoration: none;
  margin-top: 20px;
`;

const ButtonText = styled.span`
  width: 262px;
  height: 36px;
  font-family: 'Avenir Next', sans-serif;
  font-weight: 700;
  font-size: 21px;
  line-height: 150%;
  letter-spacing: -1.9%;
  color: #FFFFFF;
  text-align: center;
`;

const Home = () => {
  return (
    <HeroSection>
      <ContentWrapper>
        <WelcomeText>Welcome to Anitha Solutions.</WelcomeText>
        <SubTitle>"An Unique IT Solution"</SubTitle>
        <ExploreButton to="/about">
          <ButtonText>Explore More About Us</ButtonText>
        </ExploreButton>
      </ContentWrapper>
    </HeroSection>
  );
};

export default Home;
