// Home.jsx
import React from 'react';
import styled from 'styled-components';
import heroBg from '../assets/herobghome.png';
import homeImage1 from '../assets/homeimage1.png';
import homeImage2 from '../assets/homeimage2.png';
import homeIcon1 from '../assets/homeicon1.png';
import homeIcon2 from '../assets/homeicon2.png';
import homeIcon3 from '../assets/homeicon3.png';
import { Link } from 'react-router-dom';

const HeroSection = styled.section`
  background: url(${heroBg}) no-repeat center center;
  background-size: cover;
  min-height: 377px;
  width: 100%;
  position: relative;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  overflow: hidden;
  padding: 2rem 1rem;

  @media (max-width: 768px) {
    min-height: 300px;
    padding: 1rem;
  }
`;

const ContentWrapper = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  gap: 1.25rem;
  max-width: 1552px;
  width: 100%;
  padding: 0 1.25rem;
`;

const WelcomeText = styled.h1`
  width: 100%;
  font-family: 'Avenir Next', sans-serif;
  font-weight: 700;
  font-size: clamp(2.5rem, 5vw, 5rem);
  line-height: 1.2;
  letter-spacing: -0.022em;
  color: #FFFFFF;
  text-align: center;
  margin: 0;
`;

const SubTitle = styled.h2`
  width: 100%;
  font-family: 'Avenir Next', sans-serif;
  font-weight: 700;
  font-style: italic;
  font-size: clamp(1.75rem, 3vw, 2.875rem);
  line-height: 1.3;
  letter-spacing: -0.019em;
  color: #FFFFFF;
  text-align: center;
  margin: 0;
`;

const ExploreButton = styled(Link)`
  width: min(304px, 90%);
  padding: 1rem 2rem;
  background: linear-gradient(90deg, #FF9E42 0%, #FFC287 100%);
  border-radius: 6px;
  display: flex;
  justify-content: center;
  align-items: center;
  text-decoration: none;
  margin-top: 1.25rem;
`;

const ButtonText = styled.span`
  font-family: 'Avenir Next', sans-serif;
  font-weight: 700;
  font-size: 1.2rem;
  line-height: 1.5;
  letter-spacing: -0.019em;
  color: #FFFFFF;
  text-align: center;
`;

const AboutContainer = styled.div`
  width: 100%;
  padding: 5rem 1rem;
  display: flex;
  justify-content: center;
  overflow-x: hidden;

  @media (max-width: 768px) {
    padding: 3rem 1rem;
  }
`;

const AboutSection = styled.section`
  width: min(1258px, 100%);
  background: #EBF6FF;
  border-radius: 6px;
  position: relative;
  padding: 3.5rem 2rem;
  box-sizing: border-box;
  margin: 0 auto;

  @media (max-width: 1024px) {
    margin-left: 0;
  }
`;

const AboutHeading = styled.h2`
  width: 100%;
  max-width: 825px;
  font-family: 'Avenir Next', sans-serif;
  font-weight: 700;
  font-size: clamp(2rem, 4vw, 3rem);
  line-height: 1.3;
  letter-spacing: -0.022em;
  color: #000000;
  margin: 0;
`;

const AboutText = styled.p`
  width: 100%;
  max-width: 949px;
  font-family: 'Avenir Next', sans-serif;
  font-weight: 400;
  font-size: clamp(1rem, 2vw, 1.5rem);
  line-height: 1.5;
  letter-spacing: -0.019em;
  color: #000000;
  margin-top: 1rem;
`;

const WhyUsButton = styled(Link)`
  width: min(168px, 90%);
  padding: 1rem 1.5rem;
  background: linear-gradient(90deg, #FF9E42 0%, #FFC287 100%);
  border-radius: 6px;
  display: flex;
  justify-content: center;
  align-items: center;
  text-decoration: none;
  margin-top: 1.7rem;
`;

const AboutImage = styled.img`
  width: min(463px, 100%);
  height: auto;
  position: absolute;
  top: -2.14rem;
  right: -12.8rem;
  object-fit: cover;

  @media (max-width: 1024px) {
    position: relative;
    top: 2rem;
    right: 0;
    margin: 0 auto;
  }
`;

const ServicesContainer = styled.div`
  width: 100%;
  padding: 3rem 1rem;
  display: flex;
  justify-content: center;
`;

const ServicesSection = styled.section`
  width: min(1552px, 100%);
  background: linear-gradient(180deg, rgba(231, 245, 255, 0.7) 23.45%, rgba(80, 168, 255, 0.7) 95.17%);
  border-radius: 6px;
  padding: 1.5rem;
  box-sizing: border-box;
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const ServicesHeading = styled.h2`
  width: 100%;
  max-width: 825px;
  font-family: 'Avenir Next', sans-serif;
  font-weight: 700;
  font-size: clamp(2rem, 4vw, 3rem);
  line-height: 1.3;
  letter-spacing: -0.022em;
  color: black;
  text-align: center;
  margin-top: 1.5rem;
`;

const CardsContainer = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
  gap: 2.5rem;
  margin-top: 2.25rem;
  width: 100%;
  padding: 1rem;
`;

const ServiceCard = styled.div`
  background: #FFFFFF;
  border-radius: 6px;
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 2rem 1.5rem;
  box-sizing: border-box;
  min-height: 450px;
`;

const ServiceIcon = styled.img`
  width: clamp(100px, 15vw, 149px);
  height: auto;
  object-fit: contain;
`;

const ServiceTitle = styled.h4`
  width: 100%;
  font-family: 'Avenir Next', sans-serif;
  font-weight: 600;
  font-size: clamp(1.5rem, 3vw, 2.25rem);
  line-height: 1.15;
  letter-spacing: -0.012em;
  color: #000000;
  text-align: center;
  margin: 2rem 0 1rem 0;
`;

const ServiceDescription = styled.p`
  width: 100%;
  font-family: 'Avenir Next', sans-serif;
  font-weight: 500;
  font-size: clamp(1rem, 1.5vw, 1.25rem);
  line-height: 1.2;
  letter-spacing: -0.009em;
  color: #1E1E1E;
  text-align: center;
  margin: 0;
`;

const Home = () => {
  const services = [
    {
      icon: homeIcon1,
      title: "Software Development",
      description: "Custom software solutions tailored to your business needs"
    },
    {
      icon: homeIcon2,
      title: "IT Consulting",
      description: "Expert guidance for your technology decisions"
    },
    {
      icon: homeIcon3,
      title: "Cloud Services",
      description: "Secure and scalable cloud infrastructure solutions"
    }
  ];

  return (
    <>
      <HeroSection>
        <ContentWrapper>
          <WelcomeText>Welcome to Anitha Solutions.</WelcomeText>
          <SubTitle>"An Unique IT Solution"</SubTitle>
          <ExploreButton to="/about">
            <ButtonText>Explore More About Us</ButtonText>
          </ExploreButton>
        </ContentWrapper>
      </HeroSection>

      <AboutContainer>
        <AboutSection>
          <AboutHeading>About Anitha Solutions</AboutHeading>
          <AboutText>
            Anitha Solutions is a technology consulting and support company dedicated to helping 
            businesses meet their challenges. We believe technology solutions should solve 
            problems, not create them. We provide a full range of information technology services. 
            Anitha Solutions has been a leading provider of Software services and solutions dedicated 
            to helping businesses meet their challenges.
          </AboutText>
          <WhyUsButton to="/about">
            <ButtonText>Why Us?</ButtonText>
          </WhyUsButton>
          <AboutImage src={homeImage1} alt="About Anitha Solutions" />
        </AboutSection>
      </AboutContainer>

      <ServicesContainer>
        <ServicesSection>
          <ServicesHeading>Our Services</ServicesHeading>
          <CardsContainer>
            {services.map((service, index) => (
              <ServiceCard key={index}>
                <ServiceIcon src={service.icon} alt={service.title} />
                <ServiceTitle>{service.title}</ServiceTitle>
                <br></br>
                <ServiceDescription>{service.description}</ServiceDescription>
              </ServiceCard>
            ))}
          </CardsContainer>
        </ServicesSection>
      </ServicesContainer>

      <AboutContainer>
